import React, { Component } from "react";
import Card from "../components/Card";
import Jumbotron from "../components/Jumbotron";
// import Header from "../components/Header";
import images from "../images.json";
import Navbar from "../components/Navbar"

import { Col, Row, Container } from "../components/Grid";
// import { List, Row, Container } from "../components/Grid";
class Game extends Component {
    state = {
        clickedCards: [],
        images,
        imageIds: [],
        bestScore: 0,
        currentScore: 0,
        gameMessage: "Click an image to begin"

    };

    componentDidMount() {

    }
    checkGuess = guessId => {
      let clickArray = this.state.clickedCards;
      let bestScore = this.state.bestScore;
      let newScore = this.state.currentScore;
      newScore++;
      if (newScore > bestScore){
          bestScore = newScore;
      }
      if (clickArray.indexOf(guessId) > -1){
          this.gameOver();
      } else {
          this.setState({bestScore: bestScore, currentScore:newScore})
      }
    }

    gameOver = () => {
        alert("dang");
    }
    render() {
        return (
            <Container fluid>
                <Navbar>
                    <li>{this.state.gameMessage}</li>
                    <li>Current Score: {this.state.currentScore} |
                  Best Score: {this.state.bestScore} </li>
                </Navbar>
                <Jumbotron><h2> CLICK EM IF YOU GOT EM</h2></Jumbotron>
                {this.state.images.map(image => (
                    <Card
                        checkGuess={this.checkGuess}
                        id={image.id}
                        key={image.id}
                        name={image.name}
                        image={image.image}
                    />
                ))}


            </Container>
        );
    }
}

export default Game;
